import React, { useState } from "react";
import "./App.css";
import styled from "styled-components";
import { GlobalStyles } from "./styles/global";
import UserProfile from "./components/user_nav";
import Card from "./components/card";
import { device } from "./styles/breakpoints.js";
import data from "./data/data.json";

const Wrapper = styled.section`
  background-color: var(--bg);
  display: flex;
  justify-content: center;
  align-items: center;
  header {
    @media ${device.laptop} {
      display: flex;
      flex-direction: row;
    }
  }
`;
const CardWrapper = styled.section`
  max-width: 300px;
  display: flex;
  flex-wrap: wrap;
  flex-direction: row;
  margin-left: 1.5rem;
`;
export default function App(props) {
  const [period, setPeriod] = useState("Weekly");
  const handleClick = (e) => {
    setPeriod(e.target.innerText);
  };
  return (
    <Wrapper>
      <GlobalStyles />
      <div className="App">
        <header className="App-header">
          <UserProfile handleClick={handleClick} period={period} />
          <CardWrapper>
            {data.map((item, index) => {
              return (
                <Card
                  bgcolor={GlobalStyles.html}
                  key={index}
                  id={index}
                  title={item.title}
                  current={item["timeframes"][period.toLowerCase()]["current"]}
                  previous={
                    item["timeframes"][period.toLowerCase()]["previous"]
                  }
                  period={period}
                />
              );
            })}
          </CardWrapper>
        </header>
      </div>
    </Wrapper>
  );
}
