import { createGlobalStyle } from "styled-components";
export const GlobalStyles = createGlobalStyle`

html {
    --bg: hsl(221, 47%, 9%);
    --highlight: hsl(247, 87%, 63%);
    --play: hsl(195, 78%, 65%);
    --work: hsl(15, 100%, 70%);
    --study: hsl(348, 100%, 68%);
    --excercise: hsl(145, 58%, 55%);
    --social: hsl(264, 64%, 52%);
    --self-care: hsl(43, 84%, 65%);
    --nav: hsl(237, 46%, 23%);
    font-size:62.5%;

}
`;
