import React, { useState } from "react";
import styled from "styled-components";
import Card from "../components/card";
import UserImage from "../images/image-jeremy.png";
import { ReactComponent as workicon } from "../images/icon-work.svg";
import { ReactComponent as playicon } from "../images/icon-play.svg";
import { ReactComponent as studyicon } from "../images/icon-study.svg";
import { ReactComponent as excerciseicon } from "../images/icon-exercise.svg";
import { ReactComponent as socialicon } from "../images/icon-social.svg";
import { ReactComponent as selfcareicon } from "../images/icon-self-care.svg";
import { device } from "../styles/breakpoints.js";
import data from "../data/data.json";

const Wrapper = styled.section`
  display: flex;
  align-items: center;
  flex-direction: column;
  padding: 0 3rem;

  @media ${device.tablet} {
    gap: 1rem 3rem;
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-template-rows: 1fr 1fr;
    padding: 0 3rem;
  }

  @media ${device.laptop} {
    gap: 1rem 3rem;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    grid-template-rows: 1fr 1fr;
    padding: 0 3rem;
  }
`;

//User Nav Styles

const UserWrapper = styled.section`
  margin-top: 10rem;
  background-color: var(--nav);
  width: 32rem;
  height: 18rem;
  border-radius: 1rem;
  @media ${device.tablet} {
    width: 92%;
  }
  @media ${device.laptop} {
    margin-top: 2.5rem;
    height: 49rem;
    width: 26rem;
  }
`;

const UserBg = styled.section`
  background-color: var(--highlight);
  height: 10rem;
  width: 100%;
  border-radius: 1rem;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  @media ${device.laptop} {
    height: 34rem;
    width: 26rem;
    display: flex;
    flex-direction: column;
    text-align: left;
    align-items: left;
  }
`;
const UserInfo = styled.section`
  display: flex;
  flex-direction: column;
  margin: 0;
  h5 {
    color: white;
    font-size: 1.6rem;
    opacity: 0.5;
    text-align: left;
    margin-left: 1rem;
    font-weight: 400;
    @media ${device.laptop} {
      margin-left: 3rem;
    }
  }
  p {
    color: white;
    font-size: 2.4rem;
    margin-top: -2.5rem;
    margin-left: 1rem;
    opacity: 0.8;
    @media ${device.laptop} {
      margin-top: -4.5rem;
      margin-left: 1rem;
      font-size: 3.6rem;
      padding: 2rem;
      font-weight: 400;
    }
  }
`;

const UserPhoto = styled.img`
  width: 4.5rem;
  height: 4.5rem;
  margin: 0.5rem;
  border: 2px solid #ffffff;
  border-radius: 100rem;
  @media ${device.laptop} {
    width: 5.5rem;
    height: 5.5rem;
    margin-left: -13rem;
  }
`;

const WrapperText = styled.section`
  display: flex;
  flex-direction: row;
  justify-content: center;
  margin-top: 1.5rem;
  @media ${device.laptop} {
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
  }
  .week {
    color: white;
    opacity: 0.8;
  }

  button {
    font-size: 1.6rem;
    background: none;
    border: none;
    opacity: 0.5;
    color: var(--highlight);
    padding: 0 2rem;
    cursor: pointer;
    text-decoration: none;
    margin-top: 1.5rem;
    transition: all 0.6s ease;
    @media ${device.laptop} {
      display: flex;
      justify-content: flex-start;
      padding-left: 3rem;
    }

    &:hover {
      color: white;
      opacity: 0.9;
      transition: all 0.6s ease;
    }
  }
`;

export default function CardContainer() {
  const [workHours, setWorkHours] = useState(
    data.find((item) => item.title === "Work")
  );
  const playHours = data.find((item) => item.title === "Play");
  const studyHours = data.find((item) => item.title === "Study");
  const exerciseHours = data.find((item) => item.title === "Exercise");
  const socialHours = data.find((item) => item.title === "Social");
  const selfCarelHours = data.find((item) => item.title === "Self Care");

  const handleDailyClick = (e) => {
    e.preventDefault();
    setWorkHours({
      ...workHours,
      timeframes: {
        daily: {
          current: 6,
          previous: 5,
        },
      },
    });
  };

  return (
    <>
      <UserWrapper>
        <UserBg>
          <UserPhoto src={UserImage} />
          <UserInfo>
            <h5>Report for</h5>
            <p>Jeremy Robson</p>
          </UserInfo>
        </UserBg>
        <WrapperText>
          <button id="daily" onClick={handleDailyClick}>
            Daily
          </button>
          <button className="week">Weekly</button>
          <button id="weekly">Monthly</button>
        </WrapperText>
      </UserWrapper>
      <Wrapper>
        <Card
          bgcolor="var(--work)"
          title="Work"
          hours={`${workHours.timeframes.daily.current} hrs`}
          last={`Last Week - ${workHours.timeframes.daily.previous} hrs`}
          icon={workicon}
        ></Card>
        <Card
          bgcolor="var(--play)"
          title="Play"
          hours={`${playHours.timeframes.weekly.current} hrs`}
          last={`Last Week - ${workHours.timeframes.daily.previous} hrs`}
          icon={playicon}
        ></Card>
        <Card
          bgcolor="var(--study)"
          title="Study"
          hours={`${studyHours.timeframes.weekly.current} hrs`}
          last={`Last Week - ${studyHours.timeframes.weekly.previous} hrs`}
          icon={studyicon}
        ></Card>
        <Card
          bgcolor="var(--excercise)"
          title="Excercise"
          hours={`${exerciseHours.timeframes.weekly.current} hrs`}
          last={`Last Week - ${exerciseHours.timeframes.daily.previous} hrs`}
          icon={excerciseicon}
        ></Card>
        <Card
          bgcolor="var(--social)"
          title="Social"
          hours={`${socialHours.timeframes.weekly.current} hrs`}
          last={`Last Week - ${socialHours.timeframes.daily.previous} hrs`}
          icon={socialicon}
        ></Card>
        <Card
          bgcolor="var(--self-care)"
          title="Self Care"
          hours={`${selfCarelHours.timeframes.weekly.current} hrs`}
          last={`Last Week - ${selfCarelHours.timeframes.daily.previous} hrs`}
          icon={selfcareicon}
        ></Card>
      </Wrapper>
    </>
  );
}
